// filter array 
let filterarray = [];

// gallery card array

let galleryarray = [
    {
        id: 1,
        img: "https://andrep.my.id/assets/img/logo/andrep.png",
        nameImg: "andrep",
        name: "Home",
        link: "/view/andrep/home",
        nameButton: "Test",

        label: "<!-- <div class='label'>5% OFF</div -->"
    },

];



showgallery(galleryarray);


// create function to show card


function showgallery(curarra) {
    document.getElementById("994579").innerText = "";
    for (var i = 0; i < curarra.length; i++) {
        document.getElementById("994579").innerHTML += `
        <div class="col-6">
            <div class="card-item style-1">
                <div class="dz-media">
                    <img src="${curarra[i].img}" alt="${curarra[i].nameImg}">
                    <a href="javascript:void(0);" class="r-btn">
                        <div class="like-button"><i class="fa-regular fa-heart"></i></div>
                    </a>
                    ${curarra[i].label}
                </div>
                <div class="dz-content">
                    <h6 class="title mb-3"><a href="product.html">${curarra[i].name}</a></h6>
                    <div class="mt-2">							
                        <a class="btn btn-primary add-btn light" href="${curarra[i].link}">${curarra[i].nameButton}</a>
                    </div>							
                </div>
            </div>
        </div>

               `
    }

}

// For Live Searching Product

document.getElementById("myinput").addEventListener("keyup", function () {
    let text = document.getElementById("myinput").value;

    filterarray = galleryarray.filter(function (a) {
        if (a.name.includes(text)) {
            return a.name;
        }

    });
    if (this.value == "") {
        showgallery(galleryarray);
    }
    else {
        if (filterarray == "") {
            document.getElementById("para").style.display = 'block'
            document.getElementById("994579").innerHTML = "";
        }
        else {

            showgallery(filterarray);
            document.getElementById("para").style.display = 'none'
        }
    }

});